import express from 'express'
import cors from 'cors'

const quotes = [
    {
        id:1,
        name: 'Aristoteli',
        age:62,
        image:'data:image/jpeg;base64,',
        quote:'We are what we constantly do; Therefore, brilliance is not an action, but a habit'
    },
    {
        id:2,
        name: 'Jim Rohn',
        age:37,
        image:'data:image/jpeg;base64,',
        quote:'Discipline is the bridge between goals and achievement'
    },
    {
        id:3,
        name: 'Mark Twain',
        age:45,
        image:'data:image/jpeg;base64,',
        quote:'There are a thousand excuses for failure, but never a good reason'
    }
]



const app = express()
app.use(cors())
app.use(express.json())

const port = 6789

app.get('/', (req, res) => {
  res.send('Quote!')
})



app.get('/quotes', (req, res) => {
  let itemsToSend = quotes

   if (req.query.age) {
    itemsToSend = itemsToSend.filter(
      item => item.age === Number(req.query.age)
    )
   }

   if (req.query.name !== undefined) {
    itemsToSend = itemsToSend.filter(
      // @ts-ignore
      item => item.name.toLowerCase().includes(req.query.name.toLowerCase())
    )
   }
    res.send(itemsToSend)
  })

  app.post('/quotes', (req, res) => {
    let errors: string[] = []

    if (typeof req.body.name !== 'string') {
      errors.push('Name not provided or not a string.')
    }

    if (typeof req.body.age !== 'number') {
      errors.push('Age not provided or not a number.')
    }

    if (typeof req.body.quote !== 'string') {
      errors.push('Quote not provided or not a string.')
    }

    if (errors.length === 0) {
      const newItem = {
        id: quotes[quotes.length -1].id + 1,
        name:req.body.name,
        age: req.body.age,
        image: req.body.image,
        quote: req.body.quote
       }
    quotes.push(newItem)
    res.send(newItem)
  } else {
    res.status(400).send({errors: errors})
  }
  })

  app.get('/queryStuff', (req, res) => {
    console.log(req.query)
    res.send({message: 'Hello lovely'})
  })

  app.get('/quotes/:id', (req, res) => {
    const id = Number(req.params.id)
    const match = quotes.find(item => item.id === id)

    if (match) {
      res.send(match)
    } else {
      res.status(404).send({error: 'Item not found.'});
      
    }
    
  })

  app.get('/rendomQuoteItem', (req, res) => {
    const randomIndex = Math.floor(Math.random() * quotes.length)
    const randomItem = quotes[randomIndex]
    res.send(randomItem)
  })

app.listen(port, () => {
  console.log(`Example app listening on http://localhost:${port}`)
})